const mixin = {
    flexCenter: `
        display: flex;
        justify-contents: center;
        align-items: center;
    `,

    flexCenterColumn: `
        display: flex;
        flex-direction: column;
        justify-contents: center;
        align-items: center;
    `,
};

const colors = {
    blue: 'hsl(231, 69%, 60%)',
    red: 'hsl(0, 94%, 66%)',
    gray: 'hsl(229, 8%, 60%)',
    navy: 'hsl(229, 31%, 21%)',
};

const theme = {
    mixin,
    colors,
};

export default theme;
