const mixin = {
    flexCenter: `
        display: flex;
        justify-contents: center;
        align-items: center;
    `,

    flexCenterColumn: `
        display: flex;
        flex-direction: column;
        justify-contents: center;
        align-items: center;
    `,
};

const margins = {
    sm: '.5rem',
    base: '1rem',
    lg: '2rem',
    xl: '3rem',
};

const paddings = {
    sm: '.5rem',
    base: '1rem',
    lg: '2rem',
    xl: '3rem',
};

const size = {
    mobile: '425px',
    tablet: '768px',
    desktop: '1440px',
};

// 미디어 쿼리의 중복 코드를 줄이기위해 정의된 변수입니다
const device = {
    mobile: `@media only screen and (max-width: ${size.mobile})`,
    tablet: `@media only screen and (max-width: ${size.tablet})`,
    desktopL: `@media only screen and (max-width: ${size.desktop})`,
};

const colors = {
    blue: 'hsl(231, 69%, 60%)',
    red: 'hsl(0, 94%, 66%)',
    gray: 'hsl(229, 8%, 60%)',
    navy: 'hsl(229, 31%, 21%)',
};

const theme = {
    mixin,
    colors,
};

export default theme;
